import{_ as e,c as l,a2 as i,o as t}from"./chunks/framework.DPuwY6B9.js";const r="/assets/%E8%84%82%E6%BA%A2%E6%80%A7%E7%9A%AE%E7%82%8E.C9ugulqf.jpg",p=JSON.parse('{"title":"脂溢性皮炎","description":"","frontmatter":{},"headers":[],"relativePath":"docs/Life/Medicine/脂溢性皮炎.md","filePath":"docs/Life/Medicine/脂溢性皮炎.md","lastUpdated":1722519091000}'),o={name:"docs/Life/Medicine/脂溢性皮炎.md"};function s(d,a,n,h,c,u){return t(),l("div",null,a[0]||(a[0]=[i('<h1 id="脂溢性皮炎" tabindex="-1">脂溢性皮炎 <a class="header-anchor" href="#脂溢性皮炎" aria-label="Permalink to &quot;脂溢性皮炎&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">说明</p><p>防止丢失，本文章问备份 <a href="https://www.dxy.cn/bbs/newweb/pc/post/49348657?keywords=%E8%84%82%E6%BA%A2%E6%80%A7%E7%9A%AE%E7%82%8E" target="_blank" rel="noreferrer">参考资料</a></p></div><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>脂溢性皮炎(seborrheicdermatitis)是发生在皮脂溢出基础上的一种慢性炎症性皮肤病，表现为暗红色斑片上覆有油腻性鳞屑或痂皮，常发生于皮脂分泌活跃的部位。</p><h2 id="临床特点" tabindex="-1">临床特点 <a class="header-anchor" href="#临床特点" aria-label="Permalink to &quot;临床特点&quot;">​</a></h2><ul><li>皮损好发于头皮、颜面、胸背中央、耳后、腋窝、脐部、耻骨部及腹股沟等多脂、多毛部位；</li><li>初为毛囊性红丘疹，渐扩大融合成大小不等的黄红色浸润性斑片，境界清楚，其上覆油腻性鳞屑或痂皮；</li><li>病程呈慢性，可伴发脂溢性脱发、痤疮、酒渣鼻，皮损范围广泛者可呈红皮病；</li><li>有不同程度的瘙痒。</li></ul><h2 id="治疗原则" tabindex="-1">治疗原则 <a class="header-anchor" href="#治疗原则" aria-label="Permalink to &quot;治疗原则&quot;">​</a></h2><ul><li>多吃新鲜的蔬菜、水果及富含维生素的食品，少吃辛辣刺激性食物，控制脂肪和糖类饮食；忌用强碱性肥皂洗涤头面部，可用中性或含硫黄的肥皂、洗发乳（膏）等；勿强行剥除鳞屑。</li><li>局部治疗的原则为去脂、消炎、杀菌和止痒。</li><li>可服用各种B族维生素、抗组胺药物。</li></ul><h3 id="一般治疗" tabindex="-1">一般治疗 <a class="header-anchor" href="#一般治疗" aria-label="Permalink to &quot;一般治疗&quot;">​</a></h3><h4 id="对于病情较轻者" tabindex="-1">对于病情较轻者 <a class="header-anchor" href="#对于病情较轻者" aria-label="Permalink to &quot;对于病情较轻者&quot;">​</a></h4><ul><li>2.5%硫化硒洗剂或2%酮康唑洗剂，外用，2次/日。</li><li>3%硫黄霜或2%酮康唑霜，外用，2次/日。</li><li>复方维生素B6片，口服，3次/日。</li><li>咪唑斯汀或氯雷他定或西替利嗪，10mg/次，口服，1次/日。</li></ul><h4 id="对于病情较重者" tabindex="-1">对于病情较重者 <a class="header-anchor" href="#对于病情较重者" aria-label="Permalink to &quot;对于病情较重者&quot;">​</a></h4><ul><li>加用泼尼松，20mg/次，口服，1次/日。</li></ul><h4 id="对于合并细菌感染者" tabindex="-1">对于合并细菌感染者 <a class="header-anchor" href="#对于合并细菌感染者" aria-label="Permalink to &quot;对于合并细菌感染者&quot;">​</a></h4><ul><li>加用红霉素，1000mg/次，口服，4次/日；或罗红霉素，300mg/次，口服，2次/日。</li></ul><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><ul><li>一般在应用糖皮质激素7~10日后病情便会很快好转，这时应减少糖皮质激素的用量，维持一段时间即可停药。</li><li>严重的脂溢性皮炎或由于治疗不恰当，一部分患者会发展成为红皮病，应注意与其他红皮病相鉴别。</li><li>外用糖皮质激素一般疗程不超过2周，强效激素不能用于面部、会阴部位。</li></ul><p><img src="'+r+'" alt="脂溢性皮炎"></p>',18)]))}const b=e(o,[["render",s]]);export{p as __pageData,b as default};
